import React, { Component } from 'react';
import Slider from "react-slick";
import '../component-styles/project.css'
import ProjectCardDesign from './ProjectCardDesign';

class ProjectsCard extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 3,
      autoplay: true,
      speed: 1500,
      cssEase: 'linear',
      slidesToShow: this.props.data && this.props.data.upSell && this.props.data.upSell.length>=5?6:this.props.data&& this.props.data.upSell && this.props.data.upSell.length,
      rtl: rtl === "en" ? false : true,
      initialSlide: rtl==="en"?0:2,
      responsive: [
          {
              breakpoint: 768,
              settings: {
                  slidesToShow: 2,
                  slidesToScroll: 1,
                  infinite: true,
                  initialSlide: rtl==="en"?0:3,
                  dots: false
              }
          }
      ]
    };

    // Define an array of project card data
    const projectData = [
      { id: 1, title: 'Project 1' },
      { id: 2, title: 'Project 2' },
      { id: 3, title: 'Project 3' },
      { id: 4, title: 'Project 1' },
      { id: 5, title: 'Project 2' },
      { id: 6, title: 'Project 3' },
      { id: 7, title: 'Project 1' },
      { id: 8, title: 'Project 2' },
      { id: 9, title: 'Project 3' },
      // Add more project data as needed
    ];

    return (
      <div id='project-main-card-container'>
        <Slider className="mx-4" {...settings} ref={c => (this.slider = c)} {...settings}>
          {projectData.map((project) => (
            <div key={project.id} className='project-card-container'>
              <div className="inner-card-container">
                <ProjectCardDesign title={project.title} />
              </div>
            </div>
          ))}
        </Slider>
      </div>
    );
  }
}

export default ProjectsCard;
